import{_ as e,o as a,c as o,Q as t}from"./chunks/framework.9ad6f510.js";const y=JSON.parse('{"title":"Command Line Interface","description":"","frontmatter":{},"headers":[],"relativePath":"preparing-data/command-line-api/index.md","filePath":"preparing-data/command-line-api/index.md"}'),s={name:"preparing-data/command-line-api/index.md"},n=t('<h1 id="command-line-interface" tabindex="-1">Command Line Interface <a class="header-anchor" href="#command-line-interface" aria-label="Permalink to &quot;Command Line Interface&quot;">​</a></h1><h2 id="basic-usage" tabindex="-1">Basic Usage <a class="header-anchor" href="#basic-usage" aria-label="Permalink to &quot;Basic Usage&quot;">​</a></h2><p><code>configure_dms_viz</code> is a command-line tool designed to create a <code>.JSON</code> format specification file for <a href="https://dms-viz.github.io/" target="_blank" rel="noreferrer"><strong><code>dms-viz</code></strong></a>. You provide the data that you&#39;d like to visualize along with additional information to customize the analysis. The resulting specification file can be uploaded to <strong><code>dms-viz</code></strong> for interactive visualization of your data. Below is an overview of the process of using <code>configure_dms_viz</code>.</p><p>To format your data, you execute the <code>configure-dms-viz</code> command with the required and optional arguments as needed:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">configure-dms-viz</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">\\</span></span>\n<span class="line"><span style="color:#E1E4E8;">    </span><span style="color:#79B8FF;">--name</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">experiment_nam</span><span style="color:#E1E4E8;">e</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">\\</span></span>\n<span class="line"><span style="color:#E1E4E8;">    </span><span style="color:#79B8FF;">--input</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">input_cs</span><span style="color:#E1E4E8;">v</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">\\</span></span>\n<span class="line"><span style="color:#E1E4E8;">    </span><span style="color:#79B8FF;">--metric</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">metric_colum</span><span style="color:#E1E4E8;">n</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">\\</span></span>\n<span class="line"><span style="color:#E1E4E8;">    </span><span style="color:#79B8FF;">--structure</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">pdb_structur</span><span style="color:#E1E4E8;">e</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">\\</span></span>\n<span class="line"><span style="color:#E1E4E8;">    </span><span style="color:#79B8FF;">--sitemap</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">sitemap_cs</span><span style="color:#E1E4E8;">v</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">\\</span></span>\n<span class="line"><span style="color:#E1E4E8;">    </span><span style="color:#79B8FF;">--output</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">output_jso</span><span style="color:#E1E4E8;">n</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">\\</span></span>\n<span class="line"><span style="color:#E1E4E8;">    [optional_arguments]</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">configure-dms-viz</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">\\</span></span>\n<span class="line"><span style="color:#24292E;">    </span><span style="color:#005CC5;">--name</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">experiment_nam</span><span style="color:#24292E;">e</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">\\</span></span>\n<span class="line"><span style="color:#24292E;">    </span><span style="color:#005CC5;">--input</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">input_cs</span><span style="color:#24292E;">v</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">\\</span></span>\n<span class="line"><span style="color:#24292E;">    </span><span style="color:#005CC5;">--metric</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">metric_colum</span><span style="color:#24292E;">n</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">\\</span></span>\n<span class="line"><span style="color:#24292E;">    </span><span style="color:#005CC5;">--structure</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">pdb_structur</span><span style="color:#24292E;">e</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">\\</span></span>\n<span class="line"><span style="color:#24292E;">    </span><span style="color:#005CC5;">--sitemap</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">sitemap_cs</span><span style="color:#24292E;">v</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">\\</span></span>\n<span class="line"><span style="color:#24292E;">    </span><span style="color:#005CC5;">--output</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">output_jso</span><span style="color:#24292E;">n</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">\\</span></span>\n<span class="line"><span style="color:#24292E;">    [optional_arguments]</span></span></code></pre></div><h2 id="command-line-api" tabindex="-1">Command Line API <a class="header-anchor" href="#command-line-api" aria-label="Permalink to &quot;Command Line API&quot;">​</a></h2><ul><li><h3 id="input" tabindex="-1"><code>--input</code> <a class="header-anchor" href="#input" aria-label="Permalink to &quot;`--input`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>Path to a CSV file with site- and mutation-level data to visualize on a protein structure. <a href="/dms-viz-docs/preparing-data/data-requirements/">See details here</a> for required columns and format.</p></li><li><h3 id="name" tabindex="-1"><code>--name</code> <a class="header-anchor" href="#name" aria-label="Permalink to &quot;`--name`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>Name of the experiment/selection for the tool. For example, the antibody name or serum ID. This property is necessary for combining multiple experiments into a single file.</p></li><li><h3 id="sitemap" tabindex="-1"><code>--sitemap</code> <a class="header-anchor" href="#sitemap" aria-label="Permalink to &quot;`--sitemap`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>Path to a CSV file containing a map between reference sites in the experiment and sequential sites. <a href="/dms-viz-docs/preparing-data/data-requirements/">See details here</a> for required columns and format.</p></li><li><h3 id="metric" tabindex="-1"><code>--metric</code> <a class="header-anchor" href="#metric" aria-label="Permalink to &quot;`--metric`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>Name of the column that contains the value to visualize on the protein structure. This tells the tool which column you want to visualize on a protein strucutre.</p></li><li><h3 id="structure" tabindex="-1"><code>--structure</code> <a class="header-anchor" href="#structure" aria-label="Permalink to &quot;`--structure`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>Either an RSCB PDB ID if using a structure that can be fetched directly from the PDB (i.e. <code>&quot;6xr8&quot;</code>). Or, a path to a locally downloaded PDB file (i.e. <code>./pdb/my_custom_structure.pdb</code>).</p></li><li><h3 id="output" tabindex="-1"><code>--output</code> <a class="header-anchor" href="#output" aria-label="Permalink to &quot;`--output`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>Path to save the *.json file containing the data for the visualization tool.</p></li><li><h3 id="condition" tabindex="-1"><code>--condition</code> <a class="header-anchor" href="#condition" aria-label="Permalink to &quot;`--condition`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>If there are multiple measurements per mutation, the name of the column that contains that condition distinguishing these measurements.</p></li><li><h3 id="metric-name" tabindex="-1"><code>--metric-name</code> <a class="header-anchor" href="#metric-name" aria-label="Permalink to &quot;`--metric-name`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>The name that will show up for your metric in the plot. This let&#39;s you customize the names of your columns in your visualization. For example, if your metric column is called <code>escape_mean</code> you can rename it to <code>Escape</code> for the visualization.</p></li><li><h3 id="conditon-name" tabindex="-1"><code>--conditon_name</code> <a class="header-anchor" href="#conditon-name" aria-label="Permalink to &quot;`--conditon_name`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>The name that will show up for your condition column in the title of the plot legend. For example, if your condition column is &#39;epitope&#39;, you might rename it to be capilized as &#39;Epitope&#39; in the legend title.</p></li><li><h3 id="join-data" tabindex="-1"><code>--join-data</code> <a class="header-anchor" href="#join-data" aria-label="Permalink to &quot;`--join-data`&quot;">​</a></h3><p><code>&lt;list&gt;</code></p><p>A comma separated list of CSV file with data to join to the visualization data. This data can then be used in the visualization tooltips or filters. <a href="/dms-viz-docs/preparing-data/data-requirements/">See details here</a> for formatting requirements.</p></li><li><h3 id="tooltip-cols" tabindex="-1"><code>--tooltip-cols</code> <a class="header-anchor" href="#tooltip-cols" aria-label="Permalink to &quot;`--tooltip-cols`&quot;">​</a></h3><p><code>&lt;dict&gt;</code></p><p>A dictionary that establishes the columns that you want to show up in the tooltip in the visualization (i.e. <code>&quot;{&#39;times_seen&#39;: &#39;# Obsv&#39;, &#39;effect&#39;: &#39;Func Eff.&#39;}&quot;</code>).</p></li><li><h3 id="filter-cols" tabindex="-1"><code>--filter-cols</code> <a class="header-anchor" href="#filter-cols" aria-label="Permalink to &quot;`--filter-cols`&quot;">​</a></h3><p><code>&lt;dict&gt;</code></p><p>A dictionary that establishes the columns that you want to use as filters in the visualization (i.e. <code>&quot;{&#39;effect&#39;: &#39;Functional Effect&#39;, &#39;times_seen&#39;: &#39;Times Seen&#39;}&quot;</code>).</p></li><li><h3 id="filter-limits" tabindex="-1"><code>--filter-limits</code> <a class="header-anchor" href="#filter-limits" aria-label="Permalink to &quot;`--filter-limits`&quot;">​</a></h3><p><code> &lt;dict&gt;</code></p><p>A dictionary that establishes the range for each filter (i.e. <code>&quot;{&#39;effect&#39;: [min, max]), &#39;times_seen&#39;: [min, max]}&quot;</code>).</p></li><li><h3 id="included-chains" tabindex="-1"><code>--included-chains</code> <a class="header-anchor" href="#included-chains" aria-label="Permalink to &quot;`--included-chains`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>A space-delimited string of chain names that correspond to the chains in your PDB structure that correspond to the reference sites in your data (i.e., <code>&#39;C F M G J P&#39;</code>). This is only necesary if your PDB structure contains chains that you do not have site- and mutation-level measurements for.</p></li><li><h3 id="excluded-chains" tabindex="-1"><code>--excluded-chains</code> <a class="header-anchor" href="#excluded-chains" aria-label="Permalink to &quot;`--excluded-chains`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>A space-delimited string of chain names that should not be shown on the protein structure (i.e., <code>&#39;B L R&#39;</code>).</p></li><li><h3 id="alphabet" tabindex="-1"><code>--alphabet</code> <a class="header-anchor" href="#alphabet" aria-label="Permalink to &quot;`--alphabet`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>A string with no spaces containing all the amino acids in your experiment and their desired order (i.e. <code>&quot;RKHDEQNSTYWFAILMVGPC-*&quot;</code>).</p></li><li><h3 id="colors" tabindex="-1"><code>--colors</code> <a class="header-anchor" href="#colors" aria-label="Permalink to &quot;`--colors`&quot;">​</a></h3><p><code>&lt;list&gt;</code></p><p>A comma separated list of HEX format colors for representing different conditions, i.e. <code>&quot;#0072B2, #CC79A7, #4C3549, #009E73&quot;</code>.</p></li><li><h3 id="negative-colors" tabindex="-1"><code>--negative-colors</code> <a class="header-anchor" href="#negative-colors" aria-label="Permalink to &quot;`--negative-colors`&quot;">​</a></h3><p><code>&lt;list&gt;</code></p><p>A comma separated list of HEX format colors for representing the negative end of the scale for different conditions, i.e. <code>&quot;#0072B2, #CC79A7, #4C3549, #009E73&quot;</code>. If not provided, the inverse of each color is automatically calculated.</p></li><li><h3 id="check-pdb" tabindex="-1"><code>--check-pdb</code> <a class="header-anchor" href="#check-pdb" aria-label="Permalink to &quot;`--check-pdb`&quot;">​</a></h3><p><code>&lt;bool&gt;</code></p><p>Whether to perform checks on the provided pdb structure including checking if the &#39;included chains&#39; are present, what % of data sites are missing, and what % of wildtype residues in the data match at corresponding sites in the structure.</p></li><li><h3 id="exclude-amino-acids" tabindex="-1"><code>--exclude-amino-acids</code> <a class="header-anchor" href="#exclude-amino-acids" aria-label="Permalink to &quot;`--exclude-amino-acids`&quot;">​</a></h3><p><code>&lt;list&gt;</code></p><p>A comma separated list of amino acids that shouldn&#39;t be used to calculate the summary statistics (i.e. &quot;*, -&quot;)</p></li><li><h3 id="description" tabindex="-1"><code>--description</code> <a class="header-anchor" href="#description" aria-label="Permalink to &quot;`--description`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>A short description of the dataset that will show up in the tool if the user clicks a button for more information.</p></li><li><h3 id="title" tabindex="-1"><code>--title</code> <a class="header-anchor" href="#title" aria-label="Permalink to &quot;`--title`&quot;">​</a></h3><p><code>&lt;string&gt;</code></p><p>A short title to appear above the plot.</p></li></ul>',7),l=[n];function i(c,r,p,d,h,u){return a(),o("div",null,l)}const f=e(s,[["render",i]]);export{y as __pageData,f as default};
